import React from 'react';
import Terminal, { LineType } from 'react-terminal-ui';
import './HomePage.css';

function HomePage() {
  return (
    <div className="home-page">
      <Terminal name="Zen Terminal" colorMode="light" lineData={[
        { type: LineType.Output, value: 'Welcome to Zen.' },
        { type: LineType.Output, value: 'Type "thoughts" to go to Thoughts Villa.' },
        { type: LineType.Output, value: 'Type "mood" to go to Mood Tracker.' },
        { type: LineType.Output, value: 'Type "garden" to go to Garden.' },
      ]} />
    </div>
  );
}

export default HomePage;
