import React from 'react';
import './Garden.css';

// Example plants data array
const plants = [
    { emoji: 'ðŸŒ»', emotion: 'Happy' },
    { emoji: 'ðŸŒµ', emotion: 'Sad' },
    { emoji: 'ðŸŒº', emotion: 'Angry' },
    { emoji: 'ðŸ€', emotion: 'Relaxed' },
    // Add more plants as needed
];

function Garden({ moodMessage }) {
    const radius = 120; // Radius for the circle of emojis
    const angleStep = (2 * Math.PI) / Math.max(plants.length, 7); // Adjust angle step for circle formation

    const calculatePosition = (index) => {
        const angle = index * angleStep;
        const x = radius * Math.cos(angle) + radius; // Center the circle horizontally
        const y = radius * Math.sin(angle) + radius; // Center the circle vertically
        return { left: `${x}px`, top: `${y}px` };
    };

    return (
        <div className="garden">
            <div className="plants-container">
                {plants.map((plant, index) => (
                    <span
                        key={index}
                        className="plant"
                        style={calculatePosition(index)}
                        data-emotion={plant.emotion} // Add data-emotion attribute
                    >
                        {plant.emoji}
                    </span>
                ))}
            </div>
            <p className="mood-message">{moodMessage}</p>
            <p>
                <a href="/">Back to Home</a>
            </p>
        </div>
    );
}

export default Garden;
