import React, { useState } from 'react';
import './App.css'; // Ensure this file includes the animation CSS

function HomePage() {
  const [input, setInput] = React.useState('');
  const [backgroundColor, setBackgroundColor] = React.useState('#d3d3d3'); // Default color
  const [thoughts, setThoughts] = React.useState([]);
  const [animationClass, setAnimationClass] = React.useState('');

  const handleInputChange = (event) => {
    const value = event.target.value;
    setInput(value);
    const color = calculateBackgroundColor(value.length);
    setBackgroundColor(color);
  };

  const calculateBackgroundColor = (length) => {
    const maxLength = 100;
    const ratio = Math.min(length / maxLength, 1);
    const gray = Math.floor(211 * (1 - ratio));
    const yellow = Math.floor(255 * ratio);
    return `rgb(${gray}, ${yellow}, ${0})`;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (input.trim()) {
      setAnimationClass('explode');
      setTimeout(() => {
        setThoughts([...thoughts, input]);
        setInput('');
        setBackgroundColor('#d3d3d3');
        setAnimationClass('evaporate');
      }, 1000); // Match duration of the explode animation
    }
  };

  return (
    <div style={{ backgroundColor, height: '100vh', margin: 0 }}>
      <h1>Welcome to ZEN</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={input}
          onChange={handleInputChange}
          placeholder="Type your thoughts here..."
          rows="5"
          cols="30"
          style={{ resize: 'none', padding: '10px', fontSize: '16px' }}
        />
        <br />
        <button type="submit">Submit</button>
      </form>
      <div className={`feedback ${animationClass}`}>
        {thoughts.length > 0 && (
          <div>
            <h2>Your Submitted Thoughts:</h2>
            <ul>
              {thoughts.map((thought, index) => (
                <li key={index}>{thought}</li>
              ))}
            </ul>
          </div>
        )}
        {input && <p>Your thoughts have been noted!</p>}
      </div>
    </div>
  );
}

export default HomePage;
