import React, { useState } from 'react';
import './App.css'; // Ensure this file exists
import MoodTracker from './components/MoodTracker'; // Import MoodTracker

function App() {
  const [input, setInput] = useState('');
  const [backgroundColor, setBackgroundColor] = useState('#d3d3d3'); // Start with a dull gray color
  const [thoughts, setThoughts] = useState([]); // Array to store user thoughts

  // Function to handle text input
  const handleInputChange = (event) => {
    const value = event.target.value;
    setInput(value);

    // Calculate background color based on input length
    const color = calculateBackgroundColor(value.length);
    setBackgroundColor(color);
  };

  // Function to calculate the background color based on the length of input
  const calculateBackgroundColor = (length) => {
    const maxLength = 100; // Define a max length for color transition
    const ratio = Math.min(length / maxLength, 1); // Calculate ratio based on input length

    // Interpolate color from dull gray to bright yellow
    const gray = Math.floor(211 * (1 - ratio)); // Start with gray and fade to white
    const yellow = Math.floor(255 * ratio); // Fade to yellow

    return `rgb(${gray}, ${yellow}, ${0})`; // Create color from gray to yellow
  };

  // Function to handle submission of thoughts
  const handleSubmit = (event) => {
    event.preventDefault();

    // Add the current input to the thoughts array
    if (input.trim()) {
      setThoughts([...thoughts, input]);

      // Clear the input field after submission
      setInput('');

      // Reset background color
      setBackgroundColor('#d3d3d3');
    }
  };

  return (
    <div className="App" style={{ backgroundColor, height: '100vh', margin: 0 }}>
      <header className="App-header">
        <h1>Welcome to ZEN</h1>
        <form onSubmit={handleSubmit}>
          <textarea
            value={input}
            onChange={handleInputChange}
            placeholder="Type your thoughts here..."
            rows="5"
            cols="30"
            style={{ resize: 'none', padding: '10px', fontSize: '16px' }}
          />
          <br />
          <button type="submit">Submit</button>
        </form>
        <div className="feedback">
          {thoughts.length > 0 && (
            <div>
              <h2>Your Submitted Thoughts:</h2>
              <ul>
                {thoughts.map((thought, index) => (
                  <li key={index}>{thought}</li>
                ))}
              </ul>
            </div>
          )}
          {input && <p>Your thoughts have been noted!</p>}
        </div>
        <MoodTracker /> {/* Use MoodTracker component */}
      </header>
    </div>
  );
}

export default App;
