import React, { useEffect } from 'react';
import './ZenAnimation.css';

function ZenAnimation({ onAnimationEnd }) {
    useEffect(() => {
        const timer = setTimeout(() => {
            onAnimationEnd();
        }, 8000); // Adjust the duration to match the length of your animation

        return () => clearTimeout(timer);
    }, [onAnimationEnd]);

    return (
        <div className="zen-animation">
            <div className="circle"></div>
            <div className="zen-text">ZEN</div>
            <div className="slogan">
                <span className="word" style={{ animationDelay: '0.5s' }}>DAILY</span>
                <span className="word" style={{ animationDelay: '1s' }}>SUPPORT,</span>
                <span className="word" style={{ animationDelay: '1.5s' }}>EVERY</span>
                <span className="word" style={{ animationDelay: '2s' }}>STEP</span>
                <span className="word" style={{ animationDelay: '2.5s' }}>OF</span>
                <span className="word" style={{ animationDelay: '3s' }}>THE</span>
                <span className="word" style={{ animationDelay: '3.5s' }}>WAY</span>
            </div>
        </div>
    );
}

export default ZenAnimation;
