import React, { useState } from 'react';
import './ThoughtsBacklog.css';

function ThoughtsBacklog({ thoughts }) {
  const [selectedThought, setSelectedThought] = useState(null);
  const [showQuiz, setShowQuiz] = useState(false);
  const [quizAnswers, setQuizAnswers] = useState({ answer1: '', answer2: '' });

  const handleCardClick = (index) => {
    setSelectedThought(index);
    setShowQuiz(true);
  };

  const handleQuizSubmit = (event) => {
    event.preventDefault();
    // Simple validation for quiz answers
    if (parseInt(quizAnswers.answer1, 10) === 5 && parseInt(quizAnswers.answer2, 10) === 10) {
      setShowQuiz(false);
    }
  };

  const handleOverlayClick = () => {
    setSelectedThought(null);
    setShowQuiz(false);
  };

  return (
    <div className="thoughts-backlog">
      <h1>Your Thoughts Backlog</h1>
      <div className="flashcard-container">
        {thoughts.map((thought, index) => (
          <div
            key={index}
            className="flashcard"
            onClick={() => handleCardClick(index)}
          >
            <div className="flashcard-title">Entry {index + 1}</div>
          </div>
        ))}
      </div>

      {showQuiz && (
        <div className="modal-overlay" onClick={handleOverlayClick}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <form onSubmit={handleQuizSubmit}>
              <div className="quiz-question">
                <label>What is 2 + 3?</label>
                <input
                  type="number"
                  value={quizAnswers.answer1}
                  onChange={(e) => setQuizAnswers({ ...quizAnswers, answer1: e.target.value })}
                />
              </div>
              <div className="quiz-question">
                <label>What is 5 + 5?</label>
                <input
                  type="number"
                  value={quizAnswers.answer2}
                  onChange={(e) => setQuizAnswers({ ...quizAnswers, answer2: e.target.value })}
                />
              </div>
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}

      {selectedThought !== null && !showQuiz && (
        <div className="entry-screen" onClick={handleOverlayClick}>
          <div className="entry-content">
            <p>{thoughts[selectedThought]}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default ThoughtsBacklog;
