import React, { useEffect, useState } from 'react';
import './ZenAnimation.css';

function ZenAnimation({ onAnimationEnd }) {
    useEffect(() => {
        const timer = setTimeout(() => {
            onAnimationEnd();
        }, 5000); // Adjust the duration to match the length of your animation

        return () => clearTimeout(timer);
    }, [onAnimationEnd]);

    return (
        <div className="zen-animation">
            <h1 className="zen-title">ZEN</h1>
            <p className="zen-slogan">DAILY SUPPORT, EVERY STEP OF THE WAY.</p>
        </div>
    );
}

export default ZenAnimation;
