// export default ThoughtsVilla;import React, { useState } from 'react';
import Terminal, { TerminalOutput, TerminalInput } from 'react-terminal-ui';
import './ThoughtsVilla.css';

function ThoughtsVilla() {
  const [thoughts, setThoughts] = useState([]);
  const [terminalLineData, setTerminalLineData] = useState([
    <TerminalOutput key={0}>Enter your thoughts below:</TerminalOutput>,
  ]);

  const handleInput = (input) => {
    const newLine = <TerminalOutput key={terminalLineData.length}>{input}</TerminalOutput>;
    setThoughts([...thoughts, input]);
    setTerminalLineData([...terminalLineData, newLine]);
  };

  return (
    <div className="thoughts-villa">
      <Terminal name="Thoughts Villa" colorMode="light">
        {terminalLineData}
        <TerminalInput onInput={handleInput} />
      </Terminal>
    </div>
  );
}

export default ThoughtsVilla;
