// export default MoodTracker;import React, { useState } from 'react';
import Terminal, { TerminalOutput, TerminalInput } from 'react-terminal-ui';
import './MoodTracker.css';

function MoodTracker({ addMood }) {
  const [terminalLineData, setTerminalLineData] = useState([
    <TerminalOutput key={0}>Enter your mood below:</TerminalOutput>,
  ]);

  const handleInput = (input) => {
    const newLine = <TerminalOutput key={terminalLineData.length}>{input}</TerminalOutput>;
    addMood(input);
    setTerminalLineData([...terminalLineData, newLine]);
  };

  return (
    <div className="mood-tracker">
      <Terminal name="Mood Tracker" colorMode="light">
        {terminalLineData}
        <TerminalInput onInput={handleInput} />
      </Terminal>
    </div>
  );
}

export default MoodTracker;
