import React from 'react';
import './Garden.css';

function Garden({ plants, moodMessage }) {
    const numberOfPlants = plants.length;
    const plantsPerCircle = 7; // Number of plants per full circle
    const circleRadius = 140; // Radius of the circle (adjust as needed)
    const circles = Math.ceil(numberOfPlants / plantsPerCircle);

    const calculatePosition = (index, circleIndex) => {
        const angle = (360 / plantsPerCircle) * (index % plantsPerCircle);
        const radius = circleRadius * (circleIndex + 1); // Radius increases for each new circle
        return {
            transform: `rotate(${angle}deg) translateY(-${radius}px) rotate(${-angle}deg)`
        };
    };

    return (
        <div className="garden">
            <div className="plants-container">
                {plants.map((plant, index) => {
                    const circleIndex = Math.floor(index / plantsPerCircle);
                    return (
                        <span
                            key={index}
                            className="plant"
                            style={calculatePosition(index, circleIndex)}
                        >
                            {plant}
                        </span>
                    );
                })}
            </div>
            <p className="mood-message">{moodMessage}</p>
            <p>
                <a href="/">Back to Home</a>
            </p>
        </div>
    );
}

export default Garden;
