import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './components/HomePage';
import ThoughtsVilla from './components/ThoughtsVilla';
import MoodTracker from './components/MoodTracker';
import Navbar from './components/Navbar';
import ThoughtsBacklog from './components/ThoughtsBacklog';
import MotivationPage from './components/MotivationPage';
import './App.css';

function App() {
  const [thoughts, setThoughts] = useState([]);
  const [motivationalMessage, setMotivationalMessage] = useState('');

  const addThought = (thought) => {
    setThoughts([...thoughts, thought]);
  };

  const generateMotivationalMessage = () => {
    const messages = [
      "Keep pushing forward!",
      "You're stronger than you think!",
      "Believe in yourself!",
      "Every day is a new opportunity!",
      "You've got this!",
    ];
    const randomMessage = messages[Math.floor(Math.random() * messages.length)];
    setMotivationalMessage(randomMessage);
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <div className="content">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/thoughts-villa" element={<ThoughtsVilla addThought={addThought} generateMotivationalMessage={generateMotivationalMessage} />} />
            <Route path="/mood-tracker" element={<MoodTracker />} />
            <Route path="/thoughts-backlog" element={<ThoughtsBacklog thoughts={thoughts} />} />
            <Route path="/motivation" element={<MotivationPage motivationalMessage={motivationalMessage} thoughts={thoughts} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
