import React, { useState } from 'react';
import './MoodTracker.css';

function MoodTracker({ addMood }) {
  const [mood, setMood] = useState('');
  const [backgroundColor, setBackgroundColor] = useState('#ffffff'); // Default white background

  const handleMoodChange = (event) => {
    const selectedMood = event.target.value;
    setMood(selectedMood);

    // Change background color based on mood
    let newBackgroundColor;
    switch (selectedMood) {
      case 'happy':
        newBackgroundColor = '#ffeb3b'; // Yellow
        break;
      case 'sad':
        newBackgroundColor = '#2196f3'; // Blue
        break;
      case 'angry':
        newBackgroundColor = '#f44336'; // Red
        break;
      case 'relaxed':
        newBackgroundColor = '#4caf50'; // Green
        break;
      case 'anxious':
        newBackgroundColor = '#9c27b0'; // Purple
        break;
      default:
        newBackgroundColor = '#ffffff'; // White
    }
    setBackgroundColor(newBackgroundColor);
  };

  const handleMoodSubmit = (event) => {
    event.preventDefault();
    if (mood.trim()) {
      addMood(mood);
      setMood('');
      setBackgroundColor('#ffffff'); // Reset to default color after submission
    }
  };

  return (
    <div className="mood-tracker" style={{ backgroundColor }}>
      <h1>Mood Tracker</h1>
      <form onSubmit={handleMoodSubmit}>
        <label>
          Select your mood:
          <select value={mood} onChange={handleMoodChange}>
            <option value="">--Choose a mood--</option>
            <option value="happy">Happy üåª</option>
            <option value="sad">Sad üåßÔ∏è</option>
            <option value="angry">Angry üåã</option>
            <option value="relaxed">Relaxed üåø</option>
            <option value="anxious">Anxious üå©Ô∏è</option>
          </select>
        </label>
        <button type="submit">Submit</button>
      </form>
      <div className="plant-container">
        {mood === 'happy' && <div className="plant happy-plant">üåª</div>}
        {mood === 'sad' && <div className="plant sad-plant">üåßÔ∏è</div>}
        {mood === 'angry' && <div className="plant angry-plant">üåã</div>}
        {mood === 'relaxed' && <div className="plant relaxed-plant">üåø</div>}
        {mood === 'anxious' && <div className="plant anxious-plant">üå©Ô∏è</div>}
      </div>
    </div>
  );
}

export default MoodTracker;
