import React from 'react';
import Terminal, { TerminalOutput, TerminalInput } from 'react-terminal-ui';
import './HomePage.css';

function HomePage() {
  const [terminalLineData, setTerminalLineData] = React.useState([
    <TerminalOutput key={0}>Welcome to Zen.</TerminalOutput>,
    <TerminalOutput key={1}>Type "thoughts" to go to Thoughts Villa.</TerminalOutput>,
    <TerminalOutput key={2}>Type "mood" to go to Mood Tracker.</TerminalOutput>,
    <TerminalOutput key={3}>Type "garden" to go to Garden.</TerminalOutput>
  ]);

  return (
    <div className="home-page">
      <Terminal name="Zen Terminal" colorMode="light">
        {terminalLineData}
      </Terminal>
    </div>
  );
}

export default HomePage;
