import MoodTracker from './components/MoodTracker';
import React, { useState } from 'react';
import './App.css'; // Ensure this file exists for styling

function App() {
  const [input, setInput] = useState('');
  const [backgroundColor, setBackgroundColor] = useState('#ffffff'); // Default background color

  // Function to handle text input
  const handleInputChange = (event) => {
    const value = event.target.value;
    setInput(value);

    // Change background color based on input length (just an example)
    const color = value.length > 0 ? '#dff0d8' : '#ffffff'; // Change color if there's input
    setBackgroundColor(color);
  };

  // Function to handle submission of thoughts
  const handleSubmit = (event) => {
    event.preventDefault();
    // You can add logic here to process the input and handle state updates
    // For example, save the input to a list, clear the input field, etc.
  };

  return (
    <div className="App" style={{ backgroundColor }}>
      <header className="App-header">
        <h1>Welcome to ZEN</h1>
        <form onSubmit={handleSubmit}>
          <textarea
            value={input}
            onChange={handleInputChange}
            placeholder="Type your thoughts here..."
            rows="5"
            cols="30"
            style={{ resize: 'none', padding: '10px', fontSize: '16px' }}
          />
          <br />
          <button type="submit">Submit</button>
        </form>
        <div className="feedback">
          {/* Display feedback or additional UI elements here */}
          {input && <p>Your thoughts have been noted!</p>}
        </div>
      </header>
    </div>
  );
}

export default App;
