import React, { useState } from 'react';
import './ThoughtsBacklog.css';

function getRandomMathProblem() {
  const num1 = Math.floor(Math.random() * 10) + 1;
  const num2 = Math.floor(Math.random() * 10) + 1;
  const operator = Math.random() > 0.5 ? '+' : '-';
  const answer = operator === '+' ? num1 + num2 : num1 - num2;
  return { question: `${num1} ${operator} ${num2}`, answer };
}

function ThoughtsBacklog({ thoughts }) {
  const [selectedThought, setSelectedThought] = useState(null);
  const [showQuiz, setShowQuiz] = useState(false);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [entryContent, setEntryContent] = useState(null);

  // Generate two math problems
  const [mathProblem1, setMathProblem1] = useState(getRandomMathProblem());
  const [mathProblem2, setMathProblem2] = useState(getRandomMathProblem());

  const handleFlashcardClick = (index) => {
    setSelectedThought(index);
    setMathProblem1(getRandomMathProblem());
    setMathProblem2(getRandomMathProblem());
    setShowQuiz(true);
  };

  const handleQuizSubmit = (event) => {
    event.preventDefault();
    const correctAnswers = [mathProblem1.answer, mathProblem2.answer];
    if (
      parseInt(quizAnswers.answer1, 10) === correctAnswers[0] &&
      parseInt(quizAnswers.answer2, 10) === correctAnswers[1]
    ) {
      setEntryContent(thoughts[selectedThought]);
      setShowQuiz(false); // Close quiz
    } else {
      alert('Incorrect answers. Please try again.');
    }
  };

  const handleQuizChange = (event) => {
    setQuizAnswers({ ...quizAnswers, [event.target.name]: event.target.value });
  };

  return (
    <div className="thoughts-backlog">
      <h1>Your Thoughts Backlog</h1>
      <div className="flashcard-container">
        {thoughts.map((thought, index) => (
          <div
            key={index}
            className="flashcard"
            onClick={() => handleFlashcardClick(index)}
          >
            <div className="flashcard-title">Entry {index + 1}</div>
          </div>
        ))}
      </div>
      {showQuiz && (
        <div className="modal-overlay" onClick={() => setShowQuiz(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Math Quiz</h2>
            <form onSubmit={handleQuizSubmit}>
              <div className="quiz-question">
                <p>{mathProblem1.question} = </p>
                <input
                  type="number"
                  name="answer1"
                  value={quizAnswers.answer1 || ''}
                  onChange={handleQuizChange}
                />
              </div>
              <div className="quiz-question">
                <p>{mathProblem2.question} = </p>
                <input
                  type="number"
                  name="answer2"
                  value={quizAnswers.answer2 || ''}
                  onChange={handleQuizChange}
                />
              </div>
              <button type="submit">Submit Answers</button>
            </form>
          </div>
        </div>
      )}
      {entryContent && (
        <div className="entry-screen">
          <h2>Entry Content:</h2>
          <p>{entryContent}</p>
        </div>
      )}
    </div>
  );
}

export default ThoughtsBacklog;
