import React, { useState } from 'react';
import './ThoughtsVilla.css';

function ThoughtsVilla() {
  const [thought, setThought] = useState('');
  const [backgroundColor, setBackgroundColor] = useState('#f0f0f0'); // Initial dull color

  const handleThoughtChange = (e) => {
    const input = e.target.value;
    setThought(input);

    // Calculate color based on input length or other criteria
    const colorIntensity = Math.min(255, input.length * 10);
    const newColor = `rgb(${255 - colorIntensity}, ${255 - colorIntensity}, ${255 - colorIntensity})`; // Transition from white to black based on input length
    setBackgroundColor(newColor);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform actions on submit, like saving the thought
    setThought('');
    setBackgroundColor('#f0f0f0'); // Reset background color
  };

  return (
    <div className="thoughts-villa" style={{ backgroundColor }}>
      <h1>Thoughts Villa</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={thought}
          onChange={handleThoughtChange}
          placeholder="Type your thoughts here..."
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default ThoughtsVilla;
