import React from 'react';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import HomePage from './components/HomePage';
import ThoughtsVilla from './components/ThoughtsVilla'; // Ensure this path is correct
import MoodTracker from './components/MoodTracker'; // Ensure this path is correct

function App() {
  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/thoughts-villa" element={<ThoughtsVilla />} />
        <Route path="/mood-tracker" element={<MoodTracker />} />
      </Routes>
    </div>
  );
}

export default App;
