import React, { useRef, useEffect, useState } from 'react';
import './Garden.css';

function Garden({ plants, moodMessage }) {
  const containerRef = useRef(null);
  const [radius, setRadius] = useState(0);

  useEffect(() => {
    if (containerRef.current) {
      const containerWidth = containerRef.current.offsetWidth;
      const containerHeight = containerRef.current.offsetHeight;
      setRadius(Math.min(containerWidth, containerHeight) / 2 - 20); // 20 for some padding
    }
  }, [plants.length]);

  const angleStep = plants.length > 0 ? (2 * Math.PI) / plants.length : 0;

  const emotions = {
    'ðŸŒ»': 'Happy',
    'ðŸŒµ': 'Sad',
    'ðŸŒº': 'Angry',
    'ðŸ€': 'Relaxed'
  };

  const calculatePosition = (index) => {
    const angle = index * angleStep;
    const x = radius * Math.cos(angle) + radius;
    const y = radius * Math.sin(angle) + radius;
    return { left: `${x}px`, top: `${y}px` };
  };

  return (
    <div className="garden">
      <div className="garden-plants-container" ref={containerRef}>
        {plants.length > 0 ? (
          plants.map((plant, index) => (
            <span
              key={index}
              className="garden-plant"
              style={calculatePosition(index)}
              aria-label={`Plant representing ${emotions[plant]}`}
            >
              {plant}
              <div className="tooltip">{emotions[plant]}</div>
            </span>
          ))
        ) : (
          <p>No plants to display</p>
        )}
      </div>
      <p className="garden-mood-message">{moodMessage}</p>
      <p>
        <a href="/" className="garden-link">Back to Home</a>
      </p>
    </div>
  );
}

export default Garden;
