import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ThoughtsVilla.css';

function ThoughtsVilla() {
  const [thought, setThought] = useState('');
  const [backgroundColor, setBackgroundColor] = useState('rgb(169, 169, 169)');
  const [animationState, setAnimationState] = useState('');
  const [showMessage, setShowMessage] = useState(false);
  const [motivationalMessage, setMotivationalMessage] = useState('');
  const [showTitle, setShowTitle] = useState(true);
  const navigate = useNavigate();

  const messages = [
    "You're doing great!",
    "Keep moving forward!",
    "Believe in yourself!",
    "Every step counts!",
    "You are stronger than you think!",
    "This too shall pass!",
    "Your potential is limitless!",
  ];

  const handleThoughtChange = (e) => {
    const inputText = e.target.value;
    setThought(inputText);

    const inputLength = inputText.length;
    const maxLength = 800;
    const colorValue = Math.min(255, Math.floor((inputLength / maxLength) * 255));

    const newBackgroundColor = `rgb(${169 + colorValue}, ${169 + colorValue}, ${169 - colorValue})`;
    setBackgroundColor(newBackgroundColor);
  };

  const handleThoughtSubmit = (e) => {
    e.preventDefault();
    if (!thought.trim()) return; // Prevent submission if the text box is empty
    setShowTitle(false);
    setAnimationState('move-down');

    setTimeout(() => {
      setAnimationState('shoot-up');
    }, 1000);

    setTimeout(() => {
      setAnimationState('explode');
      const randomMessage = messages[Math.floor(Math.random() * messages.length)];
      setMotivationalMessage(randomMessage);
      setShowMessage(true);
    }, 2000);
  };

  const handleBackToHome = () => {
    navigate('/');
  };

  return (
    <div className="thoughts-villa" style={{ backgroundColor: backgroundColor }}>
      {showTitle && <h1>Thoughts Villa</h1>}
      {showMessage ? (
        <div className="motivational-message">
          <p>{motivationalMessage}</p>
          <button onClick={handleBackToHome}>Back to Home</button>
        </div>
      ) : (
        <form onSubmit={handleThoughtSubmit}>
          <textarea
            value={thought}
            onChange={handleThoughtChange}
            placeholder="Type your thoughts here..."
            className={`thought-input ${animationState}`}
          ></textarea>
          <button type="submit" className="submit-button" disabled={!thought.trim()}>
            let it go
          </button>
        </form>
      )}
    </div>
  );
}

export default ThoughtsVilla;
