import React, { useState } from 'react';
import './ThoughtsBacklog.css';

function ThoughtsBacklog({ thoughts }) {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [modalContent, setModalContent] = useState(null);

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
    setModalContent(thoughts[index]);
  };

  const handleMouseLeave = () => {
    setHoveredIndex(null);
    setModalContent(null);
  };

  return (
    <div className="thoughts-backlog">
      <h1>Your Thoughts Backlog</h1>
      <div className="flashcard-container">
        {thoughts.length > 0 ? (
          <div className="flashcards">
            {thoughts.map((thought, index) => (
              <div
                key={index}
                className="flashcard"
                onMouseEnter={() => handleMouseEnter(index)}
                onMouseLeave={handleMouseLeave}
              >
                <div className="flashcard-title">
                  Entry {index + 1}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>No thoughts logged yet.</p>
        )}
      </div>
      {modalContent && (
        <div className="modal-overlay" onClick={handleMouseLeave}>
          <div className="modal-content">
            {modalContent}
          </div>
        </div>
      )}
    </div>
  );
}

export default ThoughtsBacklog;
