import React from 'react';
import './Garden.css';

function Garden({ plants, moodMessage }) {
    const radius = 60; // Radius of each circle
    const angleStep = (2 * Math.PI) / 5; // Circle of 5 emojis

    const emotions = {
        '🌻': 'Happy',
        '🌵': 'Sad',
        '🌺': 'Angry',
        '🍀': 'Relaxed'
    };

    const calculatePosition = (index) => {
        const circleIndex = Math.floor(index / 5); // Determines which circle the emoji is in
        const positionInCircle = index % 5; // Position within the current circle

        const angle = positionInCircle * angleStep;
        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);

        const circleOffset = circleIndex * (radius * 2 + 20); // Adjust spacing between circles
        return { left: `${x}px`, top: `${y}px` };
    };

    const circles = Array.from({ length: Math.ceil(plants.length / 5) });

    return (
        <div className="garden">
            <div className="garden-plants-container">
                {circles.map((_, circleIndex) => (
                    <div key={circleIndex} className="circle">
                        {plants
                            .slice(circleIndex * 5, (circleIndex + 1) * 5)
                            .map((plant, index) => (
                                <span
                                    key={index}
                                    className="garden-plant"
                                    style={calculatePosition(circleIndex * 5 + index)}
                                >
                                    {plant}
                                    <div className="tooltip">{emotions[plant]}</div>
                                </span>
                            ))}
                    </div>
                ))}
            </div>
            <p className="garden-mood-message">{moodMessage}</p>
            <p>
                <a href="/" className="garden-link">Back to Home</a>
            </p>
        </div>
    );
}

export default Garden;
